

CREATE TABLE articles (
  id INT AUTO_INCREMENT PRIMARY KEY,
  title VARCHAR(255) NOT NULL,
  intro TEXT,
  content TEXT NOT NULL,
	imageURL VARCHAR(255),
  category_id INT,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  views INT DEFAULT 0
);
CREATE TABLE members (
    id INT AUTO_INCREMENT PRIMARY KEY,      -- 用戶 ID
    name VARCHAR(255) DEFAULT NULL,         -- 用戶名稱
    email VARCHAR(255) UNIQUE DEFAULT NULL,  -- 用戶郵箱
    password_hash varchar(100) NOT NULL
);

CREATE TABLE article_categories (
  id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  parent_id INT DEFAULT NULL,  -- 父分類 ID（NULL 為大分類，非 NULL 為小分類）
  FOREIGN KEY (parent_id) REFERENCES article_categories(id) ON DELETE CASCADE
);
CREATE TABLE article_favorites (
  like_id INT AUTO_INCREMENT PRIMARY KEY,
  member_id INT NOT NULL,
  article_id INT NOT NULL,
  FOREIGN KEY (article_id) REFERENCES articles(id) ON DELETE CASCADE,
  FOREIGN KEY (member_id) REFERENCES members(id) ON DELETE CASCADE
);
